sp_widget.client_script:
function($scope,$rootScope) {
	/* widget controller */
	var c = this;

	c.sendEvent = function(idx) {
		$rootScope.noteID = c.data.notes[idx].sys_id;
		$rootScope.$emit('selectNote',c.data.notes[idx].sys_id);
	};

	$rootScope.$on('noteUpdated',function(event,data) {
		c.server.refresh();
	});

}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	data.notes = [];
	var gr = new GlideRecord('x_snc_createnotes_note');
	gr.addQuery('user',gs.getUserID());
	gr.orderByDesc('sys_created_on');
	gr.query();
	while(gr.next())
	{
		var noteObj = {};
		$sp.getRecordDisplayValues(noteObj,gr, "number,title,sys_id");
		//noteObj.number = gr.getDisplayValue('number');
		//noteObj.title = gr.getDisplayValue('title');
		//noteObj.sys_id = gr.getDisplayValue('sys_id');
		//noteObj.number = gr.number.getDisplayValue();
		//noteObj.title = gr.title.getDisplayValue();
		//noteObj.sys_id = gr.sys_id.getDisplayValue();
		noteObj.note = gr.getValue('note').substr(0,20);
		data.notes.push(noteObj);
	}

})();
