sp_widget.client_script:
function($rootScope,$scope,spModal,spUtil) {
	/* widget controller */
	var c = this;

	$rootScope.$on('selectNote', function (event,data) {
		c.server.get({
			noteID: $rootScope.noteID,
			action: "getNote"
		}).then(function (r) {
			c.data.title = r.data.note.title;
			c.data.note = r.data.note.note;
			c.data.noteID = r.data.note.sys_id;
		});
		//console.log($rootScope.noteID);
	});

	c.updateNote = function() {
		c.server.get({
			action:'updateNote',
			noteID: c.data.noteID,
			title: c.data.title,
			note: c.data.note
		}).then(function (r) {
			//spModal.alert("Note record has been updated!");
			spUtil.addInfoMessage("Note has been updated!");
			$rootScope.$emit('noteUpdated',c.data.noteID);
			$rootScope.updatedNote = c.data.noteID;
		});
	};

}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	if(input && input.noteID)
	{
		var gr = new GlideRecord("x_snc_createnotes_note");
		gr.get(input.noteID);
		if(input.action == "getNote")
		{
			data.note = {};
			$sp.getRecordValues(data.note,gr, "title,sys_id,note");
		}
		else if(input.action == 'updateNote')
		{
			gr.title = input.title;
			gr.note = input.note;
			gr.update();
		}
	}

})();
